mapper.update:
UPDATE meeting_room
SET status = #{status}, version = version + 1
WHERE id = #{roomId} AND version = #{version}

mapper.delete:
DELETE FROM meeting_room
WHERE id IN

    #{id}

mapper.select:
SELECT id, room_code, capacity, status, is_active, equipment, location, remark
FROM meeting_room


    room_code = #{roomCode}


    and capacity >= #{capacity}


    AND status = #{status}


    AND is_active = #{isActive}


    AND location LIKE CONCAT('%', #{location}, '%')
